mapper.select:
SELECT ROW_NUMBER() OVER (ORDER BY UPT_DTE ASC) RN , COUNT(1) OVER(PARTITION BY 1) AS TOT_CNT , PAGING.* FROM ( SELECT A.* , STU.STU_TYPE1 , STU.STU_TYPE2 , 'U' AS cud , V1.LANG_CD AS STU_DTL_LANG_CD FROM T_SYS_DEF_STU_EMAIL A INNER JOIN T_SYS_DEF_STU_MAS STU ON STU.stu_cd = A.stu_cd LEFT OUTER JOIN v_sys_code V1 ON A.stu_dtl = V1.cd
A.STU_CD = #{stuCd}
A.STU_CD IS NOT NULL
AND A.STU_DTL = #{stuCd}
AND A.STU_EMAIL_UID = #{stuEmailUid}
) PAGING

mapper.insert:
        INSERT INTO T_SYS_DEF_STU_EMAIL(
             STU_EMAIL_UID
           , STU_CD
           , STU_DTL
           , REC_ROLE_CD
           , REC_ROLE_NM
           , REC_USER_CD
           , REC_USER_NM
           , REF_ROLE_CD
           , REF_ROLE_NM
           , REF_USER_CD
           , REF_USER_NM
           , TIT_KO
           , TIT_EN
           , TIT_JA
           , TIT_ZH
           , CONTENT_KO
           , CONTENT_EN
           , CONTENT_JA
           , CONTENT_ZH
           , REG_DTE
           , REG_LOGIN_ID
           , UPT_DTE
           , UPT_LOGIN_ID
        )VALUES(
            #{stuEmailUid}
	      , #{stuCd}
	      , #{stuDtl}
	      , #{recRoleCd}
	      , #{recRoleNm}
	      , #{recUserCd}
	      , #{recUserNm}
	      , #{refRoleCd}
	      , #{refRoleNm}
	      , #{refUserCd}
	      , #{refUserNm}
	      , #{titKo}
	      , #{titEn}
	      , #{titJa}
	      , #{titZh}
	      , #{contentKo}
	      , #{contentEn}
	      , #{contentJa}
	      , #{contentZh}
	      , CURRENT_TIMESTAMP
	      , 'SYSTEM'
	      , CURRENT_TIMESTAMP
	      , 'SYSTEM'
        )

mapper.update:
        UPDATE T_SYS_DEF_STU_EMAIL
        SET UPT_DTE = SYSDATE
        , UPT_LOGIN_ID = 'SYSTEM'
        
			, STU_DTL         = #{stuDtl}
		
        
			, REC_ROLE_CD     = #{recRoleCd}
		
        
			, REC_ROLE_NM     = #{recRoleNm}
		
        
			, REC_USER_CD     = #{recUserCd}
		
        
			, REC_USER_NM     = #{recUserNm}
		
        
			, REF_ROLE_CD     = #{refRoleCd}
		
        
			, REF_ROLE_NM     = #{refRoleNm}
		
        
			, REF_USER_CD     = #{refUserCd}
		
        
			, REF_USER_NM     = #{refUserNm}
		
        
			, TIT_KO          = #{titKo}
		
        
			, TIT_EN          = #{titEn}
		
        
			, TIT_JA          = #{titJa}
		
        
			, TIT_ZH          = #{titZh}
		
        
			, CONTENT_KO      = #{contentKo}
		
        
			, CONTENT_EN      = #{contentEn}
		
        
			, CONTENT_JA      = #{contentJa}
		
        
			, CONTENT_ZH      = #{contentZh}
		
        WHERE STU_EMAIL_UID = #{stuEmailUid}
