mapper.update:
UPDATE t_sys_module
SET upt_dte = CURRENT_TIMESTAMP
    , upt_login_id = 'SYSTEM'

    , module_desc = #{moduleDesc}


    , use_yn = #{useYn}

WHERE module_id = #{moduleId}

UPDATE t_sys_module_url
SET upt_dte = CURRENT_TIMESTAMP
  , upt_login_id = 'SYSTEM'
  , repre_url_yn = 'Y'
WHERE module_id = #{moduleId}
    AND ACCES_URL = #{accesUrl}

mapper.delete:
DELETE t_sys_module_url

    
        AND module_id = #{moduleId}
    
    
        AND ACCES_URL = #{accesUrl}
